import React, { useState } from 'react';
import { TextField, Button, Typography, Box, CircularProgress } from '@mui/material';

function CVForm({ onResult, onError }) {
  const [inputMode, setInputMode] = useState('url');
  const [url, setUrl] = useState('');
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleInputChange = (e, newMode) => {
    if (newMode) {
      setInputMode(newMode);
      setUrl('');
      setFile(null);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    if (onError) onError(null);
    if (onResult) onResult(null);
    let res;
    try {
      if (inputMode === 'url') {
        if (!url) {
          if (onError) onError('Please enter a LinkedIn URL.');
          setLoading(false);
          return;
        }
        res = await fetch('https://jxrgenn.app.n8n.cloud/webhook-test/558f7c16-8db5-4fe4-a396-d8faf1987be6', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ url }),
        });
      } else if (inputMode === 'file') {
        if (!file) {
          if (onError) onError('Please upload a CV file.');
          setLoading(false);
          return;
        }
        const formData = new FormData();
        formData.append('file', file);
        res = await fetch('https://jxrgenn.app.n8n.cloud/webhook-test/558f7c16-8db5-4fe4-a396-d8faf1987be6', {
          method: 'POST',
          body: formData,
        });
      }
      if (!res.ok) throw new Error('n8n request failed.');
      const data = await res.json();
      if (onResult) onResult(data);
    } catch (err) {
      if (onError) onError('Something went wrong. Try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <Box>
      <Typography variant="h4" align="center" gutterBottom>
        CV Extractor
      </Typography>
      <Typography align="center" sx={{ mb: 2 }}>
        Upload your CV or enter your LinkedIn profile.
      </Typography>
      <form onSubmit={handleSubmit}>
        {inputMode === 'url' && (
          <TextField
            label="LinkedIn Profile URL"
            fullWidth
            variant="outlined"
            value={url}
            onChange={(e) => setUrl(e.target.value)}
            sx={{ mb: 2 }}
          />
        )}
        {inputMode === 'file' && (
          <Button
            variant="outlined"
            component="label"
            fullWidth
            sx={{ mb: 2 }}
          >
            {file ? file.name : 'Upload CV (.pdf or .docx)'}
            <input
              type="file"
              accept=".pdf,.doc,.docx"
              hidden
              onChange={(e) => setFile(e.target.files[0])}
            />
          </Button>
        )}
        <Button
          type="submit"
          variant="contained"
          fullWidth
          disabled={loading}
          sx={{ py: 1.5 }}
        >
          {loading ? <CircularProgress size={24} color="inherit" /> : 'Submit'}
        </Button>
      </form>
    </Box>
  );
}

export default CVForm;
