const handleSubmit = async (e) => {
  e.preventDefault();
  setLoading(true);
  setError(null);
  setResult(null);

  let res;

  try {
    if (inputMode === 'url') {
      if (!url) {
        setError('Please enter a LinkedIn URL.');
        setLoading(false);
        return;
      }

      res = await fetch('https://jxrgen.app.n8n.cloud/webhook-test/d5b9f7ed-1cce-4c53-b817-e0796eeb889b', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ url }),
      });
    } else if (inputMode === 'file') {
      if (!file) {
        setError('Please upload a CV file.');
        setLoading(false);
        return;
      }

      const formData = new FormData();
      formData.append('file', file);

      res = await fetch('https://jxrgen.app.n8n.cloud/webhook-test/d5b9f7ed-1cce-4c53-b817-e0796eeb889b', {
        method: 'POST',
        body: formData,
      });
    }

    if (!res.ok) throw new Error('n8n request failed.');
    const data = await res.json();
    setResult(data);
  } catch (err) {
    setError('Something went wrong. Try again.');
  } finally {
    setLoading(false);
  }
};
