import React, { useState } from 'react';
import {
  Container,
  TextField,
  Button,
  Typography,
  Box,
  CircularProgress,
  Paper,
  ToggleButtonGroup,
  ToggleButton,
  Card,
  CardContent,
  Chip,
  List,
  ListItem,
  ListItemText,
  Divider,
  Stack,
  LinearProgress,
  Grid,
} from '@mui/material';

const mockJobs = [
  {
    company_name: "Acme Corp",
    job_title: "Frontend Developer",
    match_rate: 87,
    match_breakdown: [
      "Candidate has 2+ years of experience using React at XYZ Ltd",
      "Has previously worked in a remote team environment",
      "Familiar with REST APIs, matching job's integration requirements"
    ],
    missing_requirements: [
      "No experience with GraphQL",
      "Limited exposure to TypeScript"
    ],
    recommendations: [
      "Take an introductory course on GraphQL",
      "Mention any TypeScript projects in resume, if applicable"
    ],
    summary: "Strong fit for the Frontend Developer role. The candidate has relevant experience with React and remote collaboration but should brush up on GraphQL to strengthen the application."
  },
  {
    company_name: "Beta Technologies",
    job_title: "Junior Backend Engineer",
    match_rate: 72,
    match_breakdown: [
      "Knowledge of Node.js aligns with backend requirements",
      "Built multiple personal projects using MongoDB"
    ],
    missing_requirements: [
      "No commercial experience with Docker",
      "Unclear understanding of CI/CD pipelines"
    ],
    recommendations: [
      "Include GitHub link showing Dockerized projects",
      "Add coursework or self-study of CI/CD tools like Jenkins"
    ],
    summary: "Moderate fit. The candidate shows backend potential but needs to demonstrate readiness for production workflows. Worth applying with minor adjustments to resume."
  }
];

const mockCvImprovements = {
  missing_sections: ["Certifications", "Languages"],
  weak_points: [
    "No detail on technical tools used in work experience",
    "Education lacks graduation date"
  ],
  recommendations: [
    "Add a Certifications section with any completed online courses or diplomas",
    "Include a list of spoken languages and proficiency levels",
    "Expand work experience with tools, technologies, and responsibilities"
  ],
  summary: "The resume shows potential but lacks important sections and detail. Adding measurable accomplishments and structure will greatly improve its strength."
};

function JobRanking({ jobs }) {
  // Sort jobs by match_rate descending
  const sortedJobs = [...jobs].sort((a, b) => b.match_rate - a.match_rate);
  return (
    <Box>
      <Typography variant="h5" gutterBottom sx={{ fontWeight: 700, fontSize: '2rem' }}>Ranked Job Matches</Typography>
      <Stack spacing={3}>
        {sortedJobs.map((job, idx) => (
          <Card key={idx} sx={{ borderLeft: `6px solid ${job.match_rate > 80 ? '#4caf50' : job.match_rate > 60 ? '#ff9800' : '#f44336'}` }}>
            <CardContent>
              <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                <Typography variant="h6" sx={{ flexGrow: 1 }}>{job.company_name} - {job.job_title}</Typography>
                <Chip label={`Match: ${job.match_rate}%`} color={job.match_rate > 80 ? 'success' : job.match_rate > 60 ? 'warning' : 'error'} />
              </Box>
              <Box sx={{ mb: 1 }}>
                <LinearProgress variant="determinate" value={job.match_rate} sx={{ height: 8, borderRadius: 5 }} color={job.match_rate > 80 ? 'success' : job.match_rate > 60 ? 'warning' : 'error'} />
              </Box>
              <Typography variant="subtitle2" sx={{ mt: 1 }}>Match Breakdown:</Typography>
              <List dense>
                {job.match_breakdown.map((item, i) => (
                  <ListItem key={i}><ListItemText primary={item} /></ListItem>
                ))}
              </List>
              <Divider sx={{ my: 1 }} />
              <Typography variant="subtitle2">Missing Requirements:</Typography>
              <List dense>
                {job.missing_requirements.map((item, i) => (
                  <ListItem key={i}><ListItemText primary={item} /></ListItem>
                ))}
              </List>
              <Divider sx={{ my: 1 }} />
              <Typography variant="subtitle2">Recommendations:</Typography>
              <List dense>
                {job.recommendations.map((item, i) => (
                  <ListItem key={i}><ListItemText primary={item} /></ListItem>
                ))}
              </List>
              <Divider sx={{ my: 1 }} />
              <Typography variant="body2" sx={{ fontStyle: 'italic' }}>{job.summary}</Typography>
            </CardContent>
          </Card>
        ))}
      </Stack>
    </Box>
  );
}

function CvImprovements({ improvements }) {
  return (
    <Box>
      <Typography variant="h5" gutterBottom sx={{ fontWeight: 700, fontSize: '2rem' }}>CV Improvement Suggestions</Typography>
      <Card>
        <CardContent>
          <Typography variant="subtitle1" sx={{ fontWeight: 600, fontSize: '1.1rem' }}>Missing Sections:</Typography>
          <List dense>
            {improvements.missing_sections.map((section, i) => (
              <ListItem key={i}><ListItemText primary={section} /></ListItem>
            ))}
          </List>
          <Divider sx={{ my: 1 }} />
          <Typography variant="subtitle1" sx={{ fontWeight: 600, fontSize: '1.1rem' }}>Weak Points:</Typography>
          <List dense>
            {improvements.weak_points.map((point, i) => (
              <ListItem key={i}><ListItemText primary={point} /></ListItem>
            ))}
          </List>
          <Divider sx={{ my: 1 }} />
          <Typography variant="subtitle1" sx={{ fontWeight: 600, fontSize: '1.1rem' }}>Recommendations:</Typography>
          <List dense>
            {improvements.recommendations.map((rec, i) => (
              <ListItem key={i}><ListItemText primary={rec} /></ListItem>
            ))}
          </List>
          <Divider sx={{ my: 1 }} />
          <Typography variant="body2" sx={{ fontStyle: 'italic' }}>{improvements.summary}</Typography>
