      </Card>
    </Box>
  );
}

function App() {
  const [inputMode, setInputMode] = useState('url'); // 'url' or 'file'
  const [url, setUrl] = useState('');
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);

  const handleInputChange = (e, newMode) => {
    if (newMode) {
      setInputMode(newMode);
      setUrl('');
      setFile(null);
      setResult(null);
      setError(null);
    }
  };

 const handleSubmit = async (e) => {
  e.preventDefault();
  setLoading(true);
  setError(null);
  setResult(null);

  let res;

  try {
    if (inputMode === 'url') {
      if (!url) {
        setError('Please enter a LinkedIn URL.');
        setLoading(false);
        return;
      }

      res = await fetch('https://jxrgenn.app.n8n.cloud/webhook-test/558f7c16-8db5-4fe4-a396-d8faf1987be6', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ url }),
      });
    } else if (inputMode === 'file') {
      if (!file) {
        setError('Please upload a CV file.');
        setLoading(false);
        return;
      }

      const formData = new FormData();
      formData.append('file', file);

      res = await fetch('https://jxrgenn.app.n8n.cloud/webhook-test/558f7c16-8db5-4fe4-a396-d8faf1987be6', {
        method: 'POST',
        body: formData,
      });
    }

    if (!res.ok) throw new Error('n8n request failed.');
    const data = await res.json();
    setResult(data);
  } catch (err) {
    setError('Something went wrong. Try again.');
  } finally {
    setLoading(false);
  }
};
  return (
    <Container maxWidth="lg" sx={{ mt: 6, mb: 6 }}>
      <Paper sx={{ p: { xs: 2, sm: 4 }, borderRadius: 3, boxShadow: 4, maxWidth: 600, mx: 'auto' }}>
        <Typography variant="h4" align="center" gutterBottom>
          CV Extractor
        </Typography>

        <Typography align="center" sx={{ mb: 2 }}>
          Upload your CV or enter your LinkedIn profile.
        </Typography>

        <Box sx={{ display: 'flex', justifyContent: 'center', mb: 3 }}>
          <ToggleButtonGroup
            value={inputMode}
            exclusive
            onChange={handleInputChange}
            aria-label="input mode"
          >
            <ToggleButton value="url">LinkedIn URL</ToggleButton>
            <ToggleButton value="file">Upload CV</ToggleButton>
          </ToggleButtonGroup>
        </Box>

        <form onSubmit={handleSubmit}>
          {inputMode === 'url' && (
            <TextField
              label="LinkedIn Profile URL"
              fullWidth
              variant="outlined"
              value={url}
              onChange={(e) => setUrl(e.target.value)}
              sx={{ mb: 2 }}
            />
          )}

          {inputMode === 'file' && (
            <Button
              variant="outlined"
              component="label"
              fullWidth
              sx={{ mb: 2 }}
            >
              {file ? file.name : 'Upload CV (.pdf or .docx)'}
              <input
                type="file"
                accept=".pdf,.doc,.docx"
                hidden
                onChange={(e) => setFile(e.target.files[0])}
              />
            </Button>
          )}

          <Button
            type="submit"
            variant="contained"
            fullWidth
            disabled={loading}
            sx={{ py: 1.5 }}
          >
            {loading ? <CircularProgress size={24} color="inherit" /> : 'Submit'}
          </Button>
        </form>

        {error && (
          <Typography color="error" sx={{ mt: 2 }}>
            {error}
          </Typography>
        )}

        {result && (
          <Box sx={{ mt: 7 }}>
            <Grid container spacing={4} alignItems="stretch">
              <Grid item xs={12} md={6}>
                <Paper sx={{ p: { xs: 2, sm: 3 }, height: '100%', boxShadow: 2 }}>
                  <JobRanking jobs={mockJobs} />
                </Paper>
              </Grid>
              <Grid item md={0.1} sx={{ display: { xs: 'none', md: 'flex' }, justifyContent: 'center', alignItems: 'stretch' }}>
                <Divider orientation="vertical" flexItem sx={{ mx: 0.5 }} />
              </Grid>
              <Grid item xs={12} md={6}>
                <Paper sx={{ p: { xs: 2, sm: 3 }, height: '100%', boxShadow: 2 }}>
                  <CvImprovements improvements={mockCvImprovements} />
                </Paper>
              </Grid>
            </Grid>
          </Box>
        )}
      </Paper>
    </Container>
  );
}

export default App;
