import React, { useState } from 'react';

function CVForm({ onResult, onError }) {
  const [inputMode, setInputMode] = useState('url');
  const [url, setUrl] = useState('');
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);

  const webhook = 'https://jxrgenn.app.n8n.cloud/webhook-test/558f7c16-8db5-4fe4-a396-d8faf1987be6';

  const handleInputChange = (mode) => setInputMode(mode);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      let res;
      if (inputMode === 'url') {
        res = await fetch(webhook, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ url }),
        });
      } else if (inputMode === 'file' && file) {
        const formData = new FormData();
        formData.append('file', file);
        res = await fetch(webhook, {
          method: 'POST',
          body: formData,
        });
      }
      if (res && res.ok) {
        if (onResult) onResult(await res.json());
      } else {
        throw new Error('Submission failed');
      }
    } catch (err) {
      if (onError) onError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="w-full">
      <div className="flex justify-center mb-4 gap-2">
        <button
          type="button"
          className={`px-4 py-2 rounded-l-lg border ${inputMode === 'url' ? 'bg-indigo-500 text-white' : 'bg-white text-gray-700'} transition`}
          onClick={() => handleInputChange('url')}
        >
          LinkedIn URL
        </button>
        <button
          type="button"
          className={`px-4 py-2 rounded-r-lg border ${inputMode === 'file' ? 'bg-indigo-500 text-white' : 'bg-white text-gray-700'} transition`}
          onClick={() => handleInputChange('file')}
        >
          Upload CV
        </button>
      </div>
      <form onSubmit={handleSubmit} className="flex flex-col gap-4">
        {inputMode === 'url' && (
          <input
            type="url"
            className="border rounded px-3 py-2 w-full"
            placeholder="Enter LinkedIn profile URL"
            value={url}
            onChange={e => setUrl(e.target.value)}
            required
          />
        )}
        {inputMode === 'file' && (
          <input
            type="file"
            className="border rounded px-3 py-2 w-full"
            accept=".pdf,.doc,.docx,.txt"
            onChange={e => setFile(e.target.files[0])}
            required
          />
        )}
        <button
          type="submit"
          className="bg-indigo-600 hover:bg-indigo-700 text-white font-semibold py-2 px-4 rounded transition disabled:opacity-50"
          disabled={loading}
        >
          {loading ? 'Submitting...' : 'Submit'}
        </button>
      </form>
    </div>
  );
}

export default CVForm;
